<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');/** * Url helper * @version 1.0 */ if ( ! function_exists('getURL')) {	function getURL($controller = '', $method = '', $params = array()) {		$cfg =& getConfig();		$url = 'index.php';				if ($controller != '' || $method != '' || (is_array($params) && count($params)) > 0)			$url .= '?';				if ($controller != '')			$url .= $cfg['controllerParam'] . '=' . urlencode($controller) . '&';				if ($method != '')			$url .= $cfg['methodParam'] . '=' . urlencode($method) . '&';					if (is_array($params) && count($params) > 0) {			foreach ($params as $key => $value) {				$url .= urlencode($key) . '=' . urlencode($value) . '&';			}		}				$url = substr($url, 0, strlen($url) - 1);		return $url;	}}if ( ! function_exists('getModule')) {		function getModule() {		$TL =& getInstance();		return $TL->router->getController();	}	}if ( ! function_exists('getAction')) {		function getAction() {		$TL =& getInstance();		return $TL->router->getMethod();	}	}if ( ! function_exists('redirectTo')) {		function redirectTo($url) {		header("Location: {$url}");		exit();	}	}/* End of file url.php *//* Location : ./system/helpers/url.php */